            function mortgage_insurance(loan_value) {

                if (loan_value < 0.0 || loan_value > 0.95)
                {
                    return -1;
                }

                if (loan_value <= 0.80)
                {
                    return 0.0;
                }
                else if (loan_value <= 0.85)
                {
                    return 0.0180;
                }
                else if (loan_value <= 0.90)
                {
                    return 0.0240;
                }
                else if (loan_value <= 0.95)
                {
                    return 0.036;
                }

            }


